DROP TABLE IF EXISTS `t_blog_article`;
CREATE TABLE `t_blog_article`  (
  `oid` bigint(20) NOT NULL DEFAULT 0 COMMENT '文章ID',
  `article_title` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '文章标题，默认50个字符',
  `article_tags` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '帖子的标签（必填）',
  `article_author_nickname` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '发布者昵称',
  `article_author_email` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '发表文章作者的邮箱',
  `article_author_id` bigint(20) NULL DEFAULT 0 COMMENT '文章作者ID',
  `article_category_id` bigint(20) NULL DEFAULT NULL,
  `article_comment_count` int(11) NULL DEFAULT 0 COMMENT '文章评论数',
  `article_view_count` int(11) NULL DEFAULT 0 COMMENT '文章阅读数',
  `article_reward_point` int(11) NULL DEFAULT 0 COMMENT '文章打赏数',
  `article_permalink` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '文章永久连接',
  `article_originlink` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '原文连接',
  `article_latest_cmt_time` datetime(0) NULL COMMENT '最新的评论时间',
  `article_latest_cmter_name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '最新的评论人名字(名字是唯一的)',
  `article_commentable` int(2) NULL DEFAULT 1 COMMENT '能否评论1 可以评论 0 不能评论',
  `article_source` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `article_status` int(2) NULL DEFAULT 1 COMMENT '文章状态1 草稿， 2 发布',
  `article_type` int(2) NULL DEFAULT 0 COMMENT '文章类型(Java,产品等)',
  `article_good_count` int(11) NULL DEFAULT 0 COMMENT '文章点赞数',
  `article_bad_count` int(11) NULL DEFAULT 0 COMMENT '文章鄙视数量',
  `article_collect_count` int(11) NULL DEFAULT 0 COMMENT '收藏数量',
  `article_city` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '发表文章所在城市',
  `article_IP` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '发表文章IP',
  `article_perfect` int(2) NULL DEFAULT 0 COMMENT '最喜爱，置顶',
  `article_thumb` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章缩略图，默认是文章中第一张图片',
  `article_content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '内容',
  `article_content_html` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT 'html',
  `article_create_time` datetime(0) NULL DEFAULT CURRENT_TIMESTAMP(0),
  `article_update_time` datetime(0) NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP(0),
  PRIMARY KEY (`oid`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

