DROP TABLE IF EXISTS `t_blog_user`;
CREATE TABLE `t_blog_user`  (
  `oid` bigint(20) NOT NULL DEFAULT 0 COMMENT '用户ID,ID唯一，手动生成(具有标示性)',
  `user_inviter_id` bigint(20) NULL DEFAULT 0 COMMENT '邀请人ID',
  `user_name` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '姓名',
